CREATE TABLE `eaccount`.`notification` ( `id` INT NOT NULL AUTO_INCREMENT , `title` VARCHAR(255) NOT NULL , `description` VARCHAR(255) NULL , `type_id` INT NOT NULL , `sender_id` INT NOT NULL , `receiver_id` INT NULL , `is_read` TINYINT(4) NOT NULL DEFAULT '0' , `status` TINYINT(4) NOT NULL DEFAULT '1' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `deleted_at` TIMESTAMP NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;