CREATE TABLE `sikikami_eaccount`.`projects_cost_item` ( `id` INT NOT NULL AUTO_INCREMENT , `title` VARCHAR(255) NOT NULL , `description` VARCHAR(255) NULL DEFAULT NULL , `single_unit` INT NULL DEFAULT NULL , `single_cost` INT NULL DEFAULT NULL , `unit` INT NULL DEFAULT NULL , `cost` INT NULL DEFAULT NULL , `frequency` INT NOT NULL , `quater` INT NOT NULL , `status` TINYINT(4) NOT NULL DEFAULT '1' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `deleted_at` TIMESTAMP NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;